INSERT INTO `alignment` (`id`, `code`, `name`, `description`) VALUES
(1, 'LG', 'Lawful good', 'creatures can be counted on to do the right thing as expected by society. Gold dragons, paladins, and most dwarves are lawful good.'),
(2, 'NG', 'Neutral good', 'folk do the best they can to help others according to their needs. Many celestials, some cloud giants, and most gnomes are neutral good.'),
(3, 'CG', 'Chaotic good', 'creatures act as their conscience directs, with little regard for what others expect. Copper dragons, many elves, and unicorns are chaotic good.'),
(4, 'LN', 'Lawful neutral', 'individuals act in accordance with law, tradition, or personal codes. Many monks and some wizards are lawful neutral.'),
(5, 'N', 'Neutral', 'is the alignment of those who prefer to steer clear of moral questions and don\'t take sides, doing what seems best at the time. Lizard folk, most druids, and many humans are neutral.'),
(6, 'CN', 'Chaotic neutral', 'creatures follow their whims, holding their personal freedom above all else. Many barbarians and rogues, and some bards, are chaotic neutral.'),
(7, 'LE', 'Lawful evil', 'creatures methodically take what they want, within the limits of a code of tradition, loyalty, or order. Devils, blue dragons, and hobgoblins are Lawful evil.'),
(8, 'NE', 'Neutral evil', 'is the alignment of those who do whatever they can get away with, without compassion or qualms. Many drow, some cloud giants, and yugoloths are neutral evil.'),
(9, 'CE', 'Chaotic evil', 'creatures act with arbitrary violence, spurred by their greed, hatred, or bloodlust. Demons, red dragons, and orcs are chaotic evil.'),
(10, 'UA', 'Unaligned', NULL);
