INSERT INTO `damage_type` (`id`, `group_id`, `name`, `description`) VALUES
(1, 1, 'Acid', 'The corrosive spray of a black dragon’s breath and the dissolving enzymes secreted by a black pudding deal acid damage.'),
(2, 2, 'Bludgeoning', 'Blunt force attacks—hammers, falling, constriction, and the like—deal bludgeoning damage.'),
(3, 1, 'Cold', 'The infernal chill radiating from an ice devil’s spear and the frigid blast of a white dragon’s breath deal cold damage.'),
(4, 1, 'Fire', 'Red dragons breathe fire, and many spells conjure flames to deal fire damage.'),
(5, 3, 'Force', 'Force is pure magical energy focused into a damaging form. Most effects that deal force damage are spells, including magic missile and spiritual weapon.'),
(6, 1, 'Lightning', 'A lightning bolt spell and a blue dragon’s breath deal lightning damage.'),
(7, 3, 'Necrotic', 'Necrotic damage, dealt by certain undead and some spells, withers matter and even the soul.'),
(8, 2, 'Piercing', 'Puncturing and impaling attacks, including spears and monsters’ bites, deal piercing damage.'),
(9, 1, 'Poison', 'Venomous stings and the toxic gas of a green dragon’s breath deal poison damage.'),
(10, 3, 'Psychic', 'Mental abilities such as a mind flayer’s psionic blast deal psychic damage.'),
(11, 3, 'Radiant', 'Radiant damage, dealt by a cleric’s flame strike spell or an angel’s smiting weapon, sears the flesh like fire and overloads the spirit with power.'),
(12, 2, 'Slashing', 'Swords, axes, and monsters’ claws deal slashing damage.'),
(13, 1, 'Thunder', 'A concussive burst of sound, such as the effect of the thunderwave spell, deals thunder damage.');
