(3, 11, 'Abominable Yeti', NULL, 0, 0, 137, '11d12+66', 0.999, 4, 5000),
(4, 10, 'Acolyte', 'Acolytes are junior members of a clergy, usually answerable to a priest. They perform a variety of functions in a temple and are granted minor spellcasting power by their deities.', 0, 1, 9, '2d8', 0.250, 2, 50),
(5, 5, 'Adult Black Dragon', NULL, 0, 0, 195, '17d12+85', 0.999, 5, 11500),
