(15, 5, 'Adult White Dragon', NULL, 0, 0, 200, '16d12+96', 0.999, 5, 10000),
(16, 6, 'Air Elemental', 'An air elemental is a funneling cloud of whirling air with a vague semblance of a face. It can turn itself into a screaming cyclone, creating a whirlwind that batters creatures even as it flings them away.', 0, 0, 90, '12d10+24', 0.999, 3, 1800),
(17, 2, 'Allosaurus', 'The allosaurus is a predatory dinosaur of great size, strength, and speed. It can run down almost any prey over open ground, pouncing to pull creatures down with its wicked claws.', 0, 0, 51, '6d10+18', 0.999, 2, 450),
